<?php/** * Created by IntelliJ IDEA. * User: Arihant * Date: 05-05-2018 * Time: 15:43 */require 'connection.php';$rec = file_get_contents("php://input");//echo $rec;$resp = array( 'owner'=>array(),'mail'=>array(),'mob'=>array(),);$sql = "select * from firm_onwer_list where firm_id = ".$rec."";//echo $sql;$res = $conn->query($sql);if ($res->num_rows > 0){    while ($row = $res->fetch_assoc())        array_push($resp['owner'],$row);}$sql = "select * from firm_email_id where firm_id = ".$rec."";//echo $sql;$res = $conn->query($sql);if ($res->num_rows > 0){    while ($row = $res->fetch_assoc())        array_push($resp['mail'],$row);}$sql = "select * from firm_mobile_no where firm_id = ".$rec."";//echo $sql;$res = $conn->query($sql);if ($res->num_rows > 0){    while ($row = $res->fetch_assoc())        array_push($resp['mob'],$row);}echo json_encode($resp);?>