<?php/** * Created by IntelliJ IDEA. * User: Arihant * Date: 04-05-2018 * Time: 14:19 */require 'connection.php';$json_output = array();//sql query to bring the data of tran$sql = "SELECT t.id as tran_id, t.adults, t.booking_company, t.booking_date,t.booking_id,	t.cancel_policy, t.child, t.chk_in_date, t.chk_out_date, t.cust_id 	as tran_cust_id, t.hotel_booking_id, t.hotel_id as tran_hotel_id, 	t.inclusion, t.room_count, t.total, t.remarks, t.res_days,t.amount,    h.id as hotel_id, h.add1, h.add2, h.chk_in_time, h.chk_out_time,     h.city_name, h.pincode, h.state, h.xname,    c.title,c.fname,c.mname, c.lname, c.id as cust_id FROM 	tran t,cust_mst c,hotel_mst h    WHERE t.cust_id = c.id     AND t.hotel_id = h.id      order by t.id desc;";//echo $sql;$result = $conn->query($sql);//fetching the result of sql queryif ($result->num_rows > 0){    while ($tran_row = $result->fetch_assoc()) {        array_push($json_output,$tran_row);    }    echo json_encode($json_output);}else{    $json_output['no data'] = 1;    echo json_encode($json_output);}?>