<?php/** * Created by IntelliJ IDEA. * User: Arihant * Date: 15-05-2018 * Time: 17:53 */require 'connection.php';$rec = json_decode(file_get_contents("php://input"));$out = array('hotel_mob'=>array(),'tran_det'=>array(),'guest_data'=>array(),'guest_found'=>0);$sql = "select * from hotel_phn_no where hotel_id = '".$rec->hotel_id."' order by id limit 2";//echo $sql;$res = $conn->query($sql);if ($res->num_rows > 0){    while ($row = $res->fetch_assoc())        array_push($out['hotel_mob'],$row);}else{    $out['hotel_mob'] = 'NAN';}$sql = "select td.id as tran_det_id, room_type, meal_plan, ext_bed from tran_det td where ".    "td.tran_id = '".$rec->tran_id."'";//echo $sql;$res = $conn->query($sql);if ($res->num_rows > 0){    while ($row = $res->fetch_assoc()){        array_push($out['tran_det'],$row);        $sql_guest = "select * from guest_list where tran_det_id = '".$row['tran_det_id']."'";        $res_guest = $conn->query($sql_guest);        if ($res_guest->num_rows > 0){//            echo "found more data";            $out['guest_found'] = 1;            while($row_guest = $res_guest->fetch_assoc())                array_push($out['guest_data'],$row_guest);        }    }}echo json_encode($out);