<?php/** * Created by IntelliJ IDEA. * User: Arihant * Date: 26-05-2018 * Time: 17:39 */require 'connection.php';$rec = json_decode(file_get_contents("php://input"));$res = $conn->query("START TRANSACTION");$flag = 1;if ($res){    $count = sizeof($rec);    while ($count > 0){        $sql = "update cust_airline_det set xname = '".$rec[$count - 1]->xname."'".            ", mem = '".$rec[$count - 1]->mem."' ".            ", pass = '".$rec[$count - 1]->pass."' where id = '".$rec[$count - 1]->id."'";        $res = $conn->query($sql);        if (!$res){            $res = $conn->query("rollback");            $flag = 0;            echo 0;            break;        }        $count -= 1;    }    if ($flag == 1){        $res = $conn->query("commit");        echo 1;    }}else    echo 0;