<?php/** * Created by IntelliJ IDEA. * User: Arihant * Date: 10-05-2018 * Time: 15:58 */require 'connection.php';$rec = json_decode(file_get_contents("php://input"));$out = array('result'=>1,'cust_list'=>array());$sql = "update `cust_mst` set "    ."`fname` = '".$rec->fname."', `mname` = '".$rec->mname."', `lname` = '".$rec->lname."',"    ." `pass_no` = '".$rec->pass_no."', `place_of_birth` = '".$rec->place_of_birth."', `place_of_issue` = '".$rec->place_of_issue."', `expriy_date` = '".$rec->expriy_date."', "    ." `dob` = '".$rec->dob."', `gender` = '".$rec->gender."', `f_name` = '".$rec->f_name."', `m_name` = '".$rec->m_name."', `title` = '".$rec->title."', "    ."`gst_no` = '".$rec->gst_no."', `pan_no` = '".$rec->pan_no."', `email_id` = '".$rec->email_id."', "    ."`add1` = '".$rec->add1."', `add2` = '".$rec->add2."', `phn_no` = '".$rec->phn_no."',"    ." `nationality` = '".$rec->nationality."',`issue_date` = '".$rec->issue_date."',`spouse_name` = '".$rec->spouse_name."' where id = '".$rec->id."'";//echo $sql;$res = $conn->query($sql);if ($res){    $sql = "select * from cust_mst";    $result = $conn->query($sql);    if ($result->num_rows > 0){        while ($row = $result->fetch_assoc())            array_push($out['cust_list'],$row);        echo json_encode($out);    }}else{    $out['result'] = 0;    echo json_encode($out);}?>